script.code:
// Connecting to the Scheduler def retCode = 0; def nodeSourceName = variables.get("NS_name") def protocol = (variables.get("pa_port") == "443" || variables.get("pa_port") == "8443" ) ? "https" : "http" println "[+] Preparation of NodeSoure " + nodeSourceName print "(1/4) Connecting to the RM ..." rmapi.connect() println " ... OK !"
// Getting NS configuration settings def infrastructureType = "org.ow2.proactive.resourcemanager.nodesource.infrastructure.AWSEC2Infrastructure" def infrastructureParameters = [variables.get("aws_username"), //username variables.get("aws_secret"), //secret variables.get("NS_nVMs"), //N of VMs "1", //N VMs per node variables.get("image"), //image variables.get("sshUsername"), //sshUsername variables.get("sshKeyPairName"), //sshKeyPair "", //ram "", //Ncore "", //VmType variables.get("security_group"), //sg variables.get("subnet"), //subnet variables.get("rm_host_name"), //host protocol + "://" + variables.get("rm_host_name") + ":"+ variables.get("pa_port") + "/connector-iaas", //connector-iaas url protocol + "://" + variables.get("rm_host_name") + ":"+ variables.get("pa_port") + "/rest/node.jar", //node jar url "-Dproactive.useIPaddress=true", //additionalProperties" "300000", //timeout "mkdir -p /tmp/node && cd /tmp/node; if ! type -p jre/bin/java; then wget -nv -N https://s3.amazonaws.com/ci-materials/Latest_jre/jre-8u312b07-linux-x64.tar.gz; tar -xf jre-8u312b07-linux-x64.tar.gz; mv jre1.8.0_312b07/ jre; fi; wget -nv --no-check-certificate %nodeJarUrl% ; nohup jre/bin/java -Xmx600m -jar node.jar -Dproactive.communication.protocol=%protocol% -Dpython.path=%jythonPath% -Dproactive.pamr.router.address=%rmHostname% -D%instanceIdNodeProperty%=%instanceId% -r %rmUrl% -s %nodeSourceName% %nodeNamingOption% -v %credentials% -w %numberOfNodesPerInstance% %additionalProperties% &", ""] def infrastructureFileParameters = [variables.get("sshPrivateKey")] def policyType = "org.ow2.proactive.resourcemanager.nodesource.policy.EmptyPolicy" def poliyParameters = ["ALL","ME"] def policyFileParameters = [] def nodesRecoverable = "true"
// Enforcing .... try { print "(2/4) Creating NodeSource ..." rmapi.defineNodeSource(nodeSourceName,infrastructureType,(String[]) infrastructureParameters.toArray(),(String[]) infrastructureFileParameters.toArray(), policyType, (String[]) poliyParameters.toArray(), (String[]) policyFileParameters.toArray(),nodesRecoverable) println " ... OK !" print "(3/4) Deploying the NodeSource ..." rmapi.deployNodeSource(nodeSourceName) println " ... OK !" } catch (Exception e) { println " ... Error: " + e.getMessage(); retCode = 1; } finally { print "(4/4) Logging out ..." rmapi.disconnect(); println " ... OK !" } return retCode;
