script.code:
// Connecting to the Scheduler def retCode = 0; def nodeSourceName = variables.get("NS_name") def protocol = (variables.get("pa_port") == "443" || variables.get("pa_port") == "8443" ) ? "https" : "http" println "[+] Preparation of NodeSoure " + nodeSourceName print "(1/4) Connecting to the RM ..." rmapi.connect() println " ... OK !"
// Getting NS configuration settings def infrastructureType = "org.ow2.proactive.resourcemanager.nodesource.infrastructure.OpenstackInfrastructure" def infrastructureParameters = [variables.get("os_username"), //username variables.get("os_password"), //password variables.get("os_domain"), // domain variables.get("os_endpoint"), // endpoint variables.get("os_scopePrefix"), // scopePrefix variables.get("os_scopeValue"), // scopeValue variables.get("os_region"), // region variables.get("os_identityVersion"), // identityVerion "irrelevant_image", //image "irrelevant_flavor", //flavor variables.get("os_networkId"), // networkId variables.get("sshKeyPairName"), //keyPairName variables.get("NS_nVMs"), //numberOfInstance "1", //N VMs per node protocol + "://" + variables.get("rm_host_name") + ":"+ variables.get("pa_port") + "/connector-iaas", //connector-iaas url variables.get("rm_host_name"), //rmHostname protocol + "://" + variables.get("rm_host_name") + ":"+ variables.get("pa_port") + "/rest/node.jar", //node jar url "-Dproactive.useIPaddress=true", //additionalProperties" "300000", //nodesInitDelay ""] //startupScript def infrastructureFileParameters = [] def policyType = "org.ow2.proactive.resourcemanager.nodesource.policy.EmptyPolicy" def poliyParameters = ["ALL","ME"] def policyFileParameters = [] def nodesRecoverable = "true"
// Enforcing .... try { print "(2/4) Creating NodeSource ..." print rmapi.defineNodeSource(nodeSourceName,infrastructureType,(String[]) infrastructureParameters.toArray(),(String[]) infrastructureFileParameters.toArray(), policyType, (String[]) poliyParameters.toArray(), (String[]) policyFileParameters.toArray(),nodesRecoverable) println " ... OK !" print "(3/4) Deploying the NodeSource ..." rmapi.deployNodeSource(nodeSourceName) println " ... OK !" } catch (Exception e) { println " ... Error: " + e.getMessage(); retCode = 1; } finally { print "(4/4) Logging out ..." rmapi.disconnect(); println " ... OK !" } return retCode;
