script.code:
// Connecting to the Scheduler def retCode = 0; def nodeSourceName = variables.get("NS_name") def protocol = (variables.get("pa_port") == "443" || variables.get("pa_port") == "8443" ) ? "https" : "http" println "[+] Preparation of NodeSoure " + nodeSourceName print "(1/4) Connecting to the RM ..." rmapi.connect() println " ... OK !"
// Getting NS configuration settings def infrastructureType = "org.ow2.proactive.resourcemanager.nodesource.infrastructure.AwsAutoScalingInfrastructure" def infrastructureParameters = ["proactive-nodesource-", //vmGroupTagPrefix variables.get("aws_username"), //username variables.get("aws_secret"), //secret "100", //maxVms variables.get("instance_type"), //defaultInstanceType variables.get("image"), //image variables.get("sshKeyPairName"), //awsKeyPair "", //vpc variables.get("subnet"), //subnet variables.get("security_group"), //sg variables.get("region"), //region "pamr://0/", //rmUrl //use pamr://4096/ for trydevs testing variables.get("rm_host_name"), //host protocol + "://" + variables.get("rm_host_name") + ":"+ variables.get("pa_port") + "/rest/node.jar", //node jar url "-Dproactive.useIPaddress=true", //additionalProperties" "1", //maxNodePerVm "6000", //timeout "900", //cleanDelay ""] //spotPrice
def infrastructureFileParameters = [""] //externalStartupScript def policyType = "org.ow2.proactive.resourcemanager.nodesource.policy.EmptyPolicy" def poliyParameters = ["ALL","ME"] def policyFileParameters = [] def nodesRecoverable = "true"
// Enforcing .... try { print "(2/4) Creating NodeSource ..." rmapi.defineNodeSource(nodeSourceName,infrastructureType,(String[]) infrastructureParameters.toArray(),(String[]) infrastructureFileParameters.toArray(), policyType, (String[]) poliyParameters.toArray(), (String[]) policyFileParameters.toArray(),nodesRecoverable) println " ... OK !" print "(3/4) Deploying the NodeSource ..." rmapi.deployNodeSource(nodeSourceName) println " ... OK !" } catch (Exception e) { println " ... Error: " + e.toString(); retCode = 1; } finally { print "(4/4) Logging out ..." rmapi.disconnect(); println " ... OK !" } return retCode;
